TRANSFORMFIELD.EXPRESSION:
ROUND(IIF((i_PROF_MAP_CD = 'INSTALL_ACT_FEE_MATERIAL_RIGHT'  OR  i_PROF_MAP_CD='INSTALL_FEE_POB')  AND v_ACTIVITY_FLAG='I' AND NOT ISNULL(i_DURATION), i_DURATION * i_CHARGE_AMOUNT,
IIF((i_PROF_MAP_CD = 'INSTALL_ACT_FEE_MATERIAL_RIGHT'  OR  i_PROF_MAP_CD='INSTALL_FEE_POB')  AND v_ACTIVITY_FLAG='O' AND NOT ISNULL(i_DURATION),i_CHARGE_AMOUNT * DATE_DIFF(i_OUT_DATE,i_IN_DATE,'MM'),i_CHARGE_AMOUNT
)
),
2)

TABLEATTRIBUTE.VALUE:
/* Part 1 of the original SQ */
DROP TABLE $$STG_SCHEMA.TEMP_CPPH_DELTA_PRD1;
CREATE TABLE $$STG_SCHEMA.TEMP_CPPH_DELTA_PRD1 AS SELECT $$STG_SCHEMA.S_TXN_CPPH_HIST_CPPH_ID_SEQ.NEXTVAL CPPH_ID, A.DPI_CONTRACT_KEY, A.PRD_CATLG_KEY, A.PRODUCT_CODE, A.PACKAGE_CODE, I.CHARGE_AMOUNT_SUM, S.DURATION, B.PRD_CD, A.IN_DATE, A.STD_IN_DATE, A.OUT_DATE, NVL(A.CHARGE_AMOUNT,0) AS CHARGE_AMOUNT, A.QUANTITY, B.PROF_MAP_CD, B.LDR_RATE, B.CHLNG_RATE, A.EFFEC_END_DT, D.START_DATE, D.END_DATE, P.CYCLE_BEGIN, P.CYCLE_END, P.BILLING_CYCLE, F.AVG_CTRCT_DUR, G.LDR_CHLNG_CD, B.DPI_ENVRN, B.TARIF_CD, B.SVC_TYPE, A.FIRST_BILL_DATE, A.INFINIUM_GL_CODE, A.ACTIVITY_FLAG, P.BILL_DATE, --$$STG_SCHEMA.S_TXN_RESIDUAL_PRICE_FUN(A.DPI_CONTRACT_KEY,2) RESIDUAL_PRICE_VALUE, --rp.charge_amnt as RESIDUAL_PRICE_VALUE, A.USI, A.ITEM_SEQUENCE_NUMBER, B.CORE_PRD, A.CHARGE_TYPE, P.ETL_CYCLE_ID, A.CPPH_FIRST_RECUR_PER_ST_DT,A.DPI_IN_DATE,A.CA_ACTIVITY_FLAG FROM (SELECT Y.DPI_CONTRACT_KEY, Y.PRODUCT_CODE, Y.PACKAGE_CODE, Y.PRD_CATLG_KEY, Y.IN_DATE, Y.STD_IN_DATE, Y.OUT_DATE, NVL(Y.CHARGE_AMOUNT,0) AS CHARGE_AMOUNT, Y.QUANTITY, Y.EFFEC_END_DT, Y.FIRST_BILL_DATE, Y.INFINIUM_GL_CODE, Y.ACTIVITY_FLAG, Y.USI, Y.ITEM_SEQUENCE_NUMBER, Y.CHARGE_TYPE, Y.ACCOUNT_SEGMENT, Y.WIRE_CENTER, Y.CPPH_FIRST_RECUR_PER_ST_DT,Y.DPI_IN_DATE, Y.CA_ACTIVITY_FLAG FROM $$STG_SCHEMA.S_TXN_CONTRACT_PRODUCT Y WHERE Y.CURRENT_FLAG ='$$RECORD_ACTV_FLG' AND Y.ETL_CYCLE_ID = $$ETL_CYCLE_ID --AND NOT (CHARGE_TYPE='T' and ACTIVITY_FLAG='O') -- one time part filter
AND NOT (NVL(Y.CHARGE_TYPE,'null')='T' and NVL(Y.ACTIVITY_FLAG,'null')='O')
) A LEFT OUTER JOIN $$STG_SCHEMA.S_MST_PRD_CATLG B ON A.PRD_CATLG_KEY = B.PRD_CATLG_KEY AND A.STD_IN_DATE BETWEEN B.EFFEC_STRT_DATE AND B.EFFEC_END_DATE LEFT OUTER JOIN $$STG_SCHEMA.S_TXN_CONTRACT D ON A.DPI_CONTRACT_KEY = D.DPI_CONTRACT_KEY AND D.CURRENT_FLAG = '$$RECORD_ACTV_FLG' LEFT OUTER JOIN $$STG_SCHEMA.S_TXN_CUSTOMER E ON D.DPI_CUSTOMER_KEY = E.DPI_CUSTOMER_KEY AND E.CURRENT_FLAG = '$$RECORD_ACTV_FLG' /* Fix for Initial Load cross Join*/ LEFT OUTER JOIN $$STG_SCHEMA.S_TXN_CUST_CONTRACT_BILL_HIST H ON H.DPI_CUSTOMER_KEY=E.DPI_CUSTOMER_KEY and H.DPI_CONTRACT_KEY =A.DPI_CONTRACT_KEY and H.ETL_CYCLE_ID = $$ETL_CYCLE_ID LEFT OUTER JOIN (SELECT X.CYCLE_BEGIN,X.CYCLE_END,X.BILLING_CYCLE,X.BILL_DATE,X.ETL_CYCLE_ID,X.ID FROM $$STG_SCHEMA.S_PROCESSED_BILLING_CYCLE X where X.ETL_CYCLE_ID = $$ETL_CYCLE_ID) P ON H.BILL_CYCLE_ID=P.ID /* ends here */ LEFT OUTER JOIN $$STG_SCHEMA.S_REF_CUST_SEG F ON A.ACCOUNT_SEGMENT = F.CUST_SEG_CD AND F.CURRENT_FLAG = '$$RECORD_ACTV_FLG' LEFT OUTER JOIN $$STG_SCHEMA.S_REF_WRCTR G ON A.WIRE_CENTER = G.WRCTR_NO AND G.CURRENT_FLAG ='$$RECORD_ACTV_FLG' LEFT OUTER JOIN (SELECT DPI_CONTRACT_KEY,PACKAGE_CODE,SUM(CHARGE_AMOUNT) AS CHARGE_AMOUNT_SUM FROM RAESTG.S_TXN_CONTRACT_PRODUCT CH WHERE CH.CURRENT_FLAG='$$RECORD_ACTV_FLG' GROUP BY DPI_CONTRACT_KEY,PACKAGE_CODE ) I ON I.DPI_CONTRACT_KEY = A.DPI_CONTRACT_KEY AND I.PACKAGE_CODE = A.PRODUCT_CODE LEFT OUTER JOIN $$STG_SCHEMA.S_MST_ITEM_DURATION S ON A.PRODUCT_CODE=S.PRODUCT_CODE AND S.CURRENT_FLAG = '$$RECORD_ACTV_FLG' ;
/* Part 2 of the original SQ */
DROP TABLE $$STG_SCHEMA.TEMP_CPPH_DELTA_PRD2 ;
CREATE TABLE $$STG_SCHEMA.TEMP_CPPH_DELTA_PRD2 AS select sum(amnt) as charge_amnt,DPI_CONTRACT_KEY from(SELECT nvl(SUM(A.CHARGE_AMOUNT),0) as amnt,A.DPI_CONTRACT_KEY FROM $$STG_SCHEMA.S_TXN_CONTRACT_PRODUCT A LEFT OUTER JOIN $$STG_SCHEMA.S_MST_PRD_CATLG B ON A.PRD_CATLG_KEY = B.PRD_CATLG_KEY AND A.STD_IN_DATE BETWEEN B.EFFEC_STRT_DATE AND B.EFFEC_END_DATE WHERE A.CURRENT_FLAG = '$$RECORD_ACTV_FLG' AND ( (SUBSTR(TRIM(A.INFINIUM_GL_CODE),-3,3) = '141' AND B.PROF_MAP_CD='FEES_ALLOCATED')
OR (B.PROF_MAP_CD in ('INSTALL_ACT_FEE_MATERIAL_RIGHT','INSTALL_FEE_POB')) ) group by A.DPI_CONTRACT_KEY UNION ALL SELECT NVL(SUM(A.DISCOUNT_AMOUNT),0) as amnt,A.DPI_CONTRACT_KEY FROM $$STG_SCHEMA.S_TXN_CONTRACT_PROMOTION A WHERE A.CURRENT_FLAG = '$$RECORD_ACTV_FLG' AND SUBSTR(TRIM(A.INFINIUM_GL_CODE),-3,3) = '141'
group by A.DPI_CONTRACT_KEY UNION ALL SELECT nvl(SUM(A.AMOUNT),0) as amnt,A.DPI_CONTRACT_KEY FROM $$STG_SCHEMA.S_TXN_CONTRACT_REWARD_OR_AUTO A WHERE A.CURRENT_FLAG = '$$RECORD_ACTV_FLG' AND SUBSTR(TRIM(A.INFINIUM_GL_CODE),-3,3) = '141'
group by A.DPI_CONTRACT_KEY) group by dpi_contract_key;
/* Part 3 - combining part 1 & part 2, this will be the source for original mapping */
DROP TABLE $$STG_SCHEMA.TEMP_CPPH_HIST_DELTA_PRODUCT ;
CREATE TABLE $$STG_SCHEMA.TEMP_CPPH_HIST_DELTA_PRODUCT AS SELECT A.CPPH_ID, A.DPI_CONTRACT_KEY, A.PRD_CATLG_KEY, A.PRODUCT_CODE, A.PACKAGE_CODE, A.CHARGE_AMOUNT_SUM, A.DURATION, A.PRD_CD, A.IN_DATE, A.STD_IN_DATE, A.OUT_DATE, A.CHARGE_AMOUNT, A.QUANTITY, A.PROF_MAP_CD, A.LDR_RATE, A.CHLNG_RATE, A.EFFEC_END_DT, A.START_DATE, A.END_DATE, A.CYCLE_BEGIN, A.CYCLE_END, A.BILLING_CYCLE, A.AVG_CTRCT_DUR, A.LDR_CHLNG_CD, A.DPI_ENVRN, A.TARIF_CD, A.SVC_TYPE, A.FIRST_BILL_DATE, A.INFINIUM_GL_CODE, A.ACTIVITY_FLAG, A.BILL_DATE, --$$STG_SCHEMA.S_TXN_RESIDUAL_PRICE_FUN(A.DPI_CONTRACT_KEY,2) RESIDUAL_PRICE_VALUE, B.CHARGE_AMNT AS RESIDUAL_PRICE_VALUE, A.USI, A.ITEM_SEQUENCE_NUMBER, A.CORE_PRD, A.CHARGE_TYPE, A.CPPH_FIRST_RECUR_PER_ST_DT,A.DPI_IN_DATE,A.CA_ACTIVITY_FLAG FROM $$STG_SCHEMA.TEMP_CPPH_DELTA_PRD1 A LEFT OUTER JOIN $$STG_SCHEMA.TEMP_CPPH_DELTA_PRD2 B ON A.dpi_contract_key=B.dpi_contract_key WHERE A.ETL_CYCLE_ID = $$ETL_CYCLE_ID ;

DELETE FROM $$RHOP_SCHEMA.S_TXN_CPPH_HIST
WHERE
DELTA_OR_MONTHLY = 'DELTA' 
AND ETL_CYCLE_ID = $$ETL_CYCLE_ID
AND STG_TABLE_NAME = 'S_TXN_CONTRACT_PRODUCT';
COMMIT;

